import { useEffect, useState } from "react"

export const useFetch = (url) => {


    const [state, setState] = useState({data: null, loading: true, error: null});

    useEffect(() => {

        setState({
            loading: true
        })

        fetch(url)
            .then(resp => resp.json())
            .then( data => {
                setState({
                    ...state,
                    loading: false,
                    error: null,
                    data: data
                })
            })
    }, [url])


    return state;
}
