import { useEffect, useState } from "react"

export const useFetch = (url) => {


    const [state, setState] = useState({ data: null, loading: true, error: null });

    useEffect(() => {

        setState({
            ...state,
            loading: true
        })

        fetch(url)
            .then(resp => resp.json())
            .then(data => {
                setTimeout(() => {
                    setState({
                        ...state,
                        loading: false,
                        error: null,
                        data
                    }), 4000
                })
            })
    }, [url])

    return state;
}
