import React, { useEffect, useReducer } from 'react'
import { useForm } from '../../hooks/useForm'
import { todoReducer } from './todoReducer'

import './styles.css'


const init = () => {

    return JSON.parse(localStorage.getItem('todos')) || []
}


export const TodoApp = () => {

    const [todos, dispatch] = useReducer(todoReducer, [], init)


    const [{descripcion}, handleInputChange, resetValues] = useForm({
        descripcion: ""
    });

    useEffect(() => {
        localStorage.setItem('todos', JSON.stringify(todos))
    }, [todos])

    const handleDelete = (todoId) => {
        console.log(todoId);

        //Crear accion 
        //Hacer el dispatch

        const todoToDelete = {
            id: todoId,
            descripcion: todoId.descripcion,
            done: false
        }

        const actionDelete = {
            type: 'delete',
            payload: todoToDelete

        }

        dispatch(actionDelete);
        resetValues();
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        if (descripcion.trim().length <= 1 ){
            return
        }

        const newTodo = {
            id: new Date().getTime(),
            descripcion: descripcion,
            done: false
        }

        const action = {
            type: 'add',
            payload: newTodo
        }

        dispatch(action);
        resetValues()
    }

    return (
        <div>
            <h1>TodoApp ({todos.length})</h1>
            <hr />

            <div className='row'>

                <div className='col-7'>

                    <ul className='list-group list-group-flush'>
                        {
                            todos.map((todo, i) => (
                                <li
                                    key={todo.id}
                                    className="list-group-item"
                                >
                                    <p className='text-center'>{i + 1}. {todo.descripcion}</p>
                                    <button
                                        className='btn btn-danger'
                                        onClick={handleDelete}
                                    >
                                        Borrar
                                    </button>
                                </li>
                            ))
                        }
                    </ul>

                </div>

                <div className='col-5'>
                    <h4>Agregar TODO</h4>
                    <hr />

                    <form onSubmit={handleSubmit}>
                        <input
                            type="text"
                            name='descripcion'
                            placeholder='Aprender...'
                            autoComplete='off'
                            value={descripcion}
                            className='form-control'
                            onChange={ handleInputChange }
                        />
                        <button className='btn btn-outline-primary mt-1 btn-block' type='submit'>
                            Agregar
                        </button>
                    </form>
                </div>

            </div>

        </div>
    )

}
