import React from 'react'
import '../02-useEffect/effects.css'

export const FocusScreen = () => {


    const handleClick = () => {
        document.querySelector('input').select();
    }
  return (
    <div>
        <h1>FocusScreen</h1>
        <hr />

        <input
            className='form-control'
            placeholder='Su nombre'
        />

        <button 
            className='btn btn-outline-primary mt-5'
            onClick={handleClick}
        >
            Focus
        </button>

    </div>
  )
}
